import React, { useState } from 'react';
import JobTrendingChart from './JobTrendingChart';
import './App.css';

export default function App() {
  const [description, setDescription] = useState('');
  const [results, setResults] = useState([]);
  const [jobTypes, setJobTypes] = useState([]);
  const [filter, setFilter] = useState('All');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  async function handleSearch(e) {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    setResults([]);
    setJobTypes([]);

    try {
      const res = await fetch('http://localhost:5003/api/search', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ description }),
      });

      const data = await res.json();

      if (!res.ok) throw new Error(data.error || 'Error pencarian');

      if (data.message) setMessage(data.message);
      if (data.results) setResults(data.results);
      if (data.job_types) setJobTypes(data.job_types);
    } catch (err) {
      setMessage(err.message);
    } finally {
      setLoading(false);
    }
  }

  const filteredResults = filter === 'All' ? results : results.filter(r => r.job_type === filter);

  return (
    <div className="app-container">
      <h1 className="app-title">KARSO - Cari Kerja</h1>

      {/* Form pencarian */}
      <form onSubmit={handleSearch} className="search-form">
        <textarea
          rows={3}
          placeholder="Contoh: UI designer, kerja remote, startup"
          value={description}
          onChange={e => setDescription(e.target.value)}
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Mencari...' : '🔍 MENCARI'}
        </button>
      </form>

      {/* Pesan error atau info */}
      {message && <p className="message">{message}</p>}

      {/* Filter jenis kerja */}
      {jobTypes.length > 0 && (
        <div className="filter-container">
          <label htmlFor="filter">Filter Jenis Kerja:</label>
          <select
            id="filter"
            value={filter}
            onChange={e => setFilter(e.target.value)}
          >
            <option value="All">Semua</option>
            {jobTypes.map((jt, i) => (
              <option key={i} value={jt}>{jt}</option>
            ))}
          </select>
        </div>
      )}

      {/* Daftar hasil pekerjaan */}
      <div className="job-list">
        {filteredResults.map((job, i) => (
          <div key={i} className="job-card">
            <h3>{job.job_title}</h3>
            <p><strong>Perusahaan:</strong> {job.company_name}</p>
            <p><strong>Lokasi:</strong> {job.company_location}</p>
            <p><strong>Email:</strong> {job.company_email}</p>
            <p><strong>Skor Kecocokan:</strong> {(job.score * 100).toFixed(2)}%</p>
          </div>
        ))}
      </div>

      {/* Grafik tren pekerjaan direkomendasikan */}
      <div className="chart-container">
        <JobTrendingChart />
      </div>
    </div>
  );
}
