import React, { useState } from 'react';
import JobTrendingChart from './JobTrendingChart';
import './App.css';

export default function SearchPage({ onSearch }) {
  const [description, setDescription] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (description.trim() === '') return;
    onSearch(description);
  };

  return (
    <div className="container hero-section">
      <div className="hero-text">
        <img src="/logo.png" alt="Logo KARSO" style={{ width: 120, marginBottom: 20 }} />
        <h1>Kami Sedang Mencari!</h1>
        <p>Ayo gabung bersama ribuan pencari kerja lainnya dan temukan peluang terbaik untuk masa depan kariermu bersama KARSO.</p>
        {/* Scroll to form */}
        <a href="#form" className="btn btn-custom mt-3">Mulai Cari Kerja</a>
      </div>
      <div>
        <img src="/hero-illustration.png" alt="Ilustrasi Karir" className="hero-image" />
      </div>

      <div className="container form-container" id="form" style={{ marginTop: 60 }}>
        <div className="text-center mb-4">
          <h2 className="fw-bold text-pink">Cari Pekerjaan Impianmu üíº</h2>
          <p className="text-muted">Masukkan deskripsi pekerjaan yang kamu inginkan</p>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="description" className="form-label">Deskripsi pekerjaan</label>
            <textarea
              className="form-control"
              id="description"
              rows="3"
              placeholder="Contoh: UI designer, kerja remote, startup"
              value={description}
              onChange={e => setDescription(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="btn btn-search w-100 mt-2">üîç MENCARI</button>
        </form>
      </div>

      {/* Grafik tren */}
      <div style={{ maxWidth: 700, margin: '40px auto' }}>
        <JobTrendingChart />
      </div>
    </div>
  );
}
