import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';  // Menggunakan file CSS yang sama

export default function SearchPage() {
  const [description, setDescription] = useState(''); // Menyimpan input deskripsi
  const navigate = useNavigate(); // Digunakan untuk navigasi antar halaman

  // Fungsi untuk menangani pengiriman form
  const handleSubmit = (e) => {
    e.preventDefault();  // Menghindari reload halaman

    if (!description.trim()) {
      return; // Jika deskripsi kosong, tidak melakukan apa-apa
    }

    // Navigasi ke halaman /results dengan membawa state description
    navigate('/results', { state: { description } });
  };

  return (
    <div className="app-container">
      <div className="hero-section">
        <div className="hero-text" style={{ marginTop: '10px' }}>
          <img src="/logo.png" alt="Logo KARSO" style={{ width: '120px' }} />
          {/* Bagian judul dan paragraf dihapus */}
          <button className="btn-custom mt-3" onClick={() => document.getElementById('form').scrollIntoView({ behavior: 'smooth' })}>
            Mulai Cari Kerja
          </button>
        </div>
        <div>
          <img src="/hero-illustration.png" alt="Ilustrasi Karir" className="hero-image" />
        </div>
      </div>

      <div id="form">
        <div className="form-container">
          <h2 className="form-heading">Cari Pekerjaan Impianmu üíº</h2>
          <p className="form-description">Masukkan deskripsi pekerjaan yang kamu inginkan</p>
          <form onSubmit={handleSubmit}>
            <label className="form-label">Deskripsi pekerjaan</label>
            <textarea
              className="form-control"
              rows="3"
              placeholder="Contoh: UI designer, kerja remote, startup"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
            />
            <button type="submit" className="btn-search mt-2">üîç MENCARI</button>
          </form>
        </div>
      </div>
    </div>
  );
}
