import React, { useState, useRef } from 'react';
import JobTrendingChart from './JobTrendingChart';
import './App.css';

export default function App() {
  const [description, setDescription] = useState('');
  const formRef = useRef(null);

  // Scrolls to the form when button is clicked
  const scrollToForm = () => {
    formRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  async function handleSearch(e) {
    e.preventDefault();
    // Add search logic here
  }

  return (
    <div className="app-container">
      {/* Hero Section */}
      <div className="hero-section">
        <div className="hero-text">
          <img src="/logo.png" alt="Logo KARSO" />
          <h1>Kami Sedang Mencari!</h1>
          <p>Ayo gabung bersama ribuan pencari kerja lainnya dan temukan peluang terbaik untuk masa depan kariermu bersama KARSO.</p>
          <button className="btn-custom" onClick={scrollToForm}>Mulai Cari Kerja</button>
        </div>
        <div>
          <img src="/hero-illustration.png" alt="Ilustrasi Karir" className="hero-image" />
        </div>
      </div>

      {/* Input Form Section */}
      <div ref={formRef}>
        <div className="form-container">
          <h2 className="text-pink">Cari Pekerjaan Impianmu üíº</h2>
          <form onSubmit={handleSearch}>
            <textarea
              placeholder="Contoh: UI designer, kerja remote, startup"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
            />
            <button type="submit" className="btn-search">üîç MENCARI</button>
          </form>
        </div>
      </div>

      {/* Trending Chart Section */}
      <div className="chart-section">
        <JobTrendingChart />
      </div>
    </div>
  );
}
