import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './App.css';

export default function JobDetailPage() {
  const location = useLocation();
  const navigate = useNavigate();

  // Ambil data detail dari state yang dikirim via navigate
  const job = location.state?.job;

  // Kalau tidak ada data job, kembali ke halaman hasil
  if (!job) {
    navigate('/results');
    return null;
  }

  return (
    <div className="app-container">
      <button
        onClick={() => navigate(-1)} // kembali ke halaman sebelumnya
        className="btn-back"
        style={{ marginBottom: 20 }}
      >
        ðŸ”™ Kembali ke Hasil
      </button>

      <h2 style={{ color: '#ec407a' }}>{job.job_title}</h2>
      <p><strong>Perusahaan:</strong> {job.company_name}</p>
      <p><strong>Lokasi:</strong> {job.company_location}</p>
      <p><strong>Email:</strong> {job.company_email}</p>
      <p><strong>Ukuran Perusahaan:</strong> {job.company_size || 'Tidak tersedia'}</p>
      <p><strong>Jam Kerja:</strong> {job.work_hours || 'Tidak tersedia'}</p>
      <p><strong>Kualifikasi:</strong></p>
      <p>{job.job_qualifications || 'Tidak tersedia'}</p>
      <p><strong>Deskripsi Pekerjaan:</strong></p>
      <p>{job.job_desc || 'Tidak tersedia'}</p>
    </div>
  );
}
