import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

import SearchPage from './SearchPage';
import ResultPage from './ResultPage';
import JobDetailPage from './JobDetailPage';
import TrendingPage from './TrendingPage';  // Pastikan file ini ada

// Komponen Navbar dengan tombol Tren Pencarian
function Navbar() {
  return (
    <nav style={{
      padding: '10px 20px',
      backgroundColor: '#fce4ec',
      display: 'flex',
      alignItems: 'center',
      gap: '20px',
      boxShadow: '0 2px 5px rgba(236, 64, 122, 0.2)'
    }}>
      {/* Logo di kiri */}
      <Link to="/" style={{ textDecoration: 'none', color: '#ec407a', fontWeight: '900', fontSize: '1.5rem' }}>
        KARSO
      </Link>

      {/* Tombol Tren Pencarian */}
      <Link 
        to="/trending"
        className="btn-custom"
        style={{
          marginLeft: 'auto',  // dorong ke kanan
          padding: '8px 25px',
          borderRadius: '30px',
          backgroundColor: '#ec407a',
          color: 'white',
          fontWeight: '600',
          textDecoration: 'none',
          transition: 'background-color 0.3s ease',
        }}
        onMouseEnter={e => e.target.style.backgroundColor = '#d81b60'}
        onMouseLeave={e => e.target.style.backgroundColor = '#ec407a'}
      >
        Tren Pencarian
      </Link>
    </nav>
  );
}

export default function App() {
  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<SearchPage />} />
        <Route path="/results" element={<ResultPage />} />
        <Route path="/job-detail" element={<JobDetailPage />} />
        <Route path="/trending" element={<TrendingPage />} />
      </Routes>
    </Router>
  );
}
