import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import JobTrendingChart from './JobTrendingChart';
import './App.css';

export default function SearchPage() {
  const [description, setDescription] = useState('');
  const navigate = useNavigate();

  // Fungsi submit form: navigasi ke halaman hasil sambil bawa deskripsi pencarian
  const handleSubmit = e => {
    e.preventDefault();
    if (!description.trim()) return; // validasi kosong

    // Navigasi ke /results dan kirim description lewat state
    navigate('/results', { state: { description } });
  };

  return (
    <div className="app-container">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-text">
          <img src="/logo.png" alt="Logo KARSO" className="hero-logo" />
          <h1>Kami Sedang Mencari!</h1>
          <p>Ayo gabung bersama ribuan pencari kerja lainnya dan temukan peluang terbaik untuk masa depan kariermu bersama KARSO.</p>
          {/* Tombol Mulai Cari Kerja tetap ada */}
          <button className="btn-hero" onClick={() => {
            // scroll ke form jika mau
            const form = document.getElementById('form-section');
            if (form) form.scrollIntoView({ behavior: 'smooth' });
          }}>Mulai Cari Kerja</button>
        </div>
        <div className="hero-image-container">
          <img src="/hero-illustration.png" alt="Ilustrasi Karir" className="hero-image" />
        </div>
      </section>

      {/* Form Section */}
      <section className="form-section" id="form-section">
        <div className="form-container">
          <h2>Cari Pekerjaan Impianmu üíº</h2>
          <p>Masukkan deskripsi pekerjaan yang kamu inginkan</p>
          <form onSubmit={handleSubmit}>
            <label htmlFor="description" className="form-label">Deskripsi pekerjaan</label>
            <textarea
              id="description"
              rows={3}
              placeholder="Contoh: UI designer, kerja remote, startup"
              value={description}
              onChange={e => setDescription(e.target.value)}
              required
            />
            <button type="submit" className="btn-search">üîç MENCARI</button>
          </form>

          {/* Grafik tren tetap ada di halaman ini */}
          <div style={{ marginTop: 50 }}>
            <JobTrendingChart />
          </div>
        </div>
      </section>
    </div>
  );
}
