import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './App.css';

export default function JobDetailPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const job = location.state?.job;

  if (!job) {
    return (
      <div className="app-container">
        <p>Data pekerjaan tidak ditemukan.</p>
        <button className="btn-back" onClick={() => navigate(-1)}>Kembali</button>
      </div>
    );
  }

  return (
    <div className="app-container job-detail-container">
      <div className="job-detail-header">
        <img src="/logo.png" alt="Logo KARSO" className="result-logo" />
        <h2>{job.job_title}</h2>
        <p><strong>Perusahaan:</strong> {job.company_name || 'Tidak tersedia'}</p>
        <p><strong>Lokasi:</strong> {job.company_location || 'Tidak tersedia'}</p>
        <p><strong>Email:</strong> {job.company_email || 'Tidak tersedia'}</p>
      </div>

      <div className="job-detail-content">
        <div className="job-detail-section">
          <h3>Jam Kerja</h3>
          <p>{job.work_hours || 'Tidak tersedia'}</p>
        </div>

        <div className="job-detail-section">
          <h3>Kualifikasi</h3>
          <p>{job.job_qualifications || 'Tidak tersedia'}</p>
        </div>

        <div className="job-detail-section">
          <h3>Deskripsi Pekerjaan</h3>
          <p>{job.job_desc || 'Tidak tersedia'}</p>
        </div>
      </div>

      <div className="back-button-container">
        <button className="btn-back" onClick={() => navigate(-1)}>ðŸ”™ Kembali</button>
      </div>
    </div>
  );
}
