import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

export default function TrendingPage() {
  const [trendData, setTrendData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5003/api/job-trending') // Sesuaikan URL backend Anda
      .then((res) => {
        if (!res.ok) throw new Error('Gagal mengambil data tren');
        return res.json();
      })
      .then((data) => {
        // Format data untuk chart.js
        const chartData = {
          labels: data.map(item => item.job_title),
          datasets: [
            {
              label: 'Frekuensi Pekerjaan Direkomendasikan',
              data: data.map(item => Number(item.freq)),
              fill: false,
              borderColor: 'rgba(236, 64, 122, 0.7)',
              backgroundColor: 'rgba(236, 64, 122, 0.7)',
              tension: 0.3,
            },
          ],
        };
        setTrendData(chartData);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <p style={{ textAlign: 'center' }}>Memuat data tren...</p>;
  if (error) return <p style={{ color: 'red', textAlign: 'center' }}>Error: {error}</p>;
  if (!trendData) return null;

  return (
    <div style={{ maxWidth: 700, margin: '20px auto', backgroundColor: '#fff', borderRadius: 12, padding: 20, boxShadow: '0 6px 25px rgba(0,0,0,0.1)' }}>
      <h2 style={{ color: '#ec407a', textAlign: 'center', marginBottom: 20 }}>
        Tren Pekerjaan Direkomendasikan
      </h2>
      <Line
        data={trendData}
        options={{
          responsive: true,
          plugins: {
            legend: {
              position: 'top',
            },
            title: {
              display: true,
              text: 'Frekuensi Pekerjaan Direkomendasikan',
              font: {
                size: 18,
              },
            },
          },
          scales: {
            y: {
              beginAtZero: true,
              ticks: {
                stepSize: 1,
              },
            },
          },
        }}
      />
    </div>
  );
}
